import React, { useRef} from 'react'
import axios from 'axios'
import './Register.css'

const Register = () => {
  const firstname = useRef('')
  const lastname = useRef('')
  const email = useRef('')
  const password = useRef('')
  const confirmPassword = useRef('')

  const submitHandler = () =>{
    if(password === confirmPassword){
      let data = JSON.stringify({
        "firstName": firstname.current.focus(),
        "lastName": lastname.current.focus(),
        "email": email.current.focus(),
        "password": password.current.focus()
      });

      let config = {
        method: 'post',
        url: 'http://localhost:6000/create',
        headers: { 
          'Content-Type': 'application/json'
        },
        data : data
      };

      axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
      })
      .catch(function (error) {
        console.log(error);
      });

    }else{
      alert("Passowrd do not match")
    }


  }

  return (
    <div>
        <h1 className='title'>Register</h1>
         <form className='register' onSubmit={submitHandler}>
            <div className="input-box">
            <label htmlFor="input">First Name</label>
            <input 
            className='input'
            ref={firstname}
            type="text" 
            placeholder='Enter your first name'
            />
            </div>
            <div className="input-box">
            <label htmlFor="input">Last Name</label>
          <input 
          className='input'
          ref={lastname}
          placeholder='Enter Your Last Name'
          type="text" 
          />
            </div>
            <div className="input-box">
            <label htmlFor="input">Email</label>
          <input 
          className='input'
          ref={email}
          placeholder='Enter Your email'
          type="email" 
          />
            </div>
            <div className="input-box">
            <label htmlFor="input">Password</label>
          <input 
          className='input'
          placeholder='Password'
          ref={password}
          type="password" 
          />
            </div>
            <div className="input-box">
            <label htmlFor="input">Confirm Password</label>
          <input 
          className='input'
          ref={confirmPassword}
          placeholder='Password'
          type="password" 
          />
            </div>





            <button className="btn" type='submit'>Register</button>
        </form>
    </div>
  )
}

export default Register
