import React from 'react'
import { useState,useRef } from 'react'

const Post = () => {
    const inputElement = useRef();

    const submitHandle = () => {
        inputElement.current.focus();
      };
  return (
    <div>
        <form>
            <input 
            className='input'
            type="text" 
            placeholder='Enter your thoughts fearlessly'
            label="Opinion"
            />
            <button 
            onClick={submitHandle}
            className="btn" 
            type='submit'>Post</button>
        </form>
    </div>
  )
}

export default Post
